import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";

function ViewStores() {
  const [stores, setStores] = useState([]);
  const navigate = useNavigate();

  // const [showActionButtons, setShowActionButtons] = useState(true);

  useEffect(() => {
    // Fetch data from the backend server
    axios
      .get("http://localhost:3007/view-stores")
      .then((response) => {
        //setInvitations(response.data);
        const data = response.data;
        if (Array.isArray(data)) {
          setStores(data);
        } else {
          console.error("Stores data is not an array:", data);
        }
      })
      .catch((error) => {
        console.error("Failed to fetch Stores:", error);
      });
  }, []);

  return (
    <div className="container">
      <style
        dangerouslySetInnerHTML={{
          __html:
            "\n  .table-container {\n    max-width: 100%;\n    overflow-x: auto;\n  }\n\n  .table {\n    width: 100%;\n    table-layout: fixed;\n    border-collapse: collapse;\n  }\n\n  .table td {\n    word-wrap: break-word;\n    max-width: 200px; /* Adjust the value as needed */\n    white-space: normal;\n  }\n",
        }}
      />

      <table className="table">
        <caption>List of Franchise Stores</caption>
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Tax Registration Number</th>
            {/* <th scope="col">Invitation</th>*/}
            {/* <th scope="col">Branch Manager Username</th>  */}
            <th scope="col">Store DID</th>
            <th scope="col">Status</th>
            <th scope="col">Action</th>
          </tr>
        </thead>
        <tbody>
          {stores.map((store, index) => (
            <tr key={store._id}>
              <th scope="row">{index + 1}</th>
              <td>{store.tax_reg_num}</td>
              {/* <td>{JSON.stringify(invitation.invitation)}</td>*/}
              {/* <td>
                <a href={invitation.invitation_url}>invitation</a>
              </td> */}
              <td>{store.did}</td>
              <td>{store.status}</td>

              <td>
                {store.status === "non-legitimized" ? (
                  <div>
                    <button type="button" className="btn btn-danger">
                      Legitimize
                    </button>
                    &nbsp;&nbsp;
                    <button type="button" className="btn btn-warning" disabled>
                      Send Invitation
                    </button>
                  </div>
                ) : (
                  <div>
                    <button type="button" className="btn btn-danger" disabled>
                      Legitimize
                    </button>
                    &nbsp;&nbsp;
                    <button type="button" className="btn btn-warning">
                    Send Invitation
                    </button>
                  </div>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ViewStores;
