import axios from "axios";
import React, { useEffect, useState } from "react";

function Viewstores() {
  const [branches, setBranches] = useState([]);
  const [invitations, setInvitations] = useState([]);

  useEffect(() => {
    // Fetch branches data from the backend server
    axios
      .get("http://localhost:3007/view-stores")
      .then((response) => {
        const data = response.data;
        if (Array.isArray(data)) {
          setBranches(data);
        } else {
          console.error("branches data is not an array:", data);
        }
      })
      .catch((error) => {
        console.error("Failed to fetch stores:", error);
      });
  }, []);

  useEffect(() => {
    // Fetch invitations data from the backend server
    axios
      .get("http://localhost:3007/view-invitations")
      .then((response) => {
        const data = response.data;
        if (Array.isArray(data)) {
          setInvitations(data);
        } else {
          console.error("Invitations data is not an array:", data);
        }
      })
      .catch((error) => {
        console.error("Failed to fetch invitations:", error);
      });
  }, []);

  const sendInvitation = (holderDID) => {
    // Send invitation to a holder based on their DID
    axios
      .post("http://localhost:3007/create-invitation", {
        did: holderDID,
        status: "pending",
      })
      .then((response) => {
        console.log("Invitation sent successfully");
        // Update the invitation status in the local state
        const updatedInvitations = invitations.map((invitation) =>
          invitation.holderDID === holderDID
            ? { ...invitation, status: "pending" }
            : invitation
        );
        setInvitations(updatedInvitations);
      })
      .catch((error) => {
        console.error("Failed to send invitation:", error);
      });
  };

  const isInvitationPending = (holderDID) => {
    const invitation = invitations.find(
      (invitation) => invitation.holderDID === holderDID
    );
    return invitation && invitation.status === "pending";
  };

  return (
    <div className="container">
      <style
        dangerouslySetInnerHTML={{
          __html: `
          .table-container {
            max-width: 100%;
            overflow-x: auto;
          }

          .table {
            width: 100%;
            table-layout: fixed;
            border-collapse: collapse;
          }

          .table td {
            word-wrap: break-word;
            max-width: 200px; /* Adjust the value as needed */
            white-space: normal;
          }
          `,
        }}
      />

      <table className="table">
        <caption>List of Franchise stores</caption>
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Branch Name</th>
            <th scope="col">Branch Manager DID</th>
            <th scope="col">Branch Manager Name</th>
            <th scope="col">Action</th>
          </tr>
        </thead>
        <tbody>
          {branches.map((branch, index) => (
            <tr key={branch._id}>
              <th scope="row">{index + 1}</th>
              <td>{branch.branchName}</td>
              <td>{branch.holderDID}</td>
              <td>{branch.branchManager}</td>
              <td>
                {isInvitationPending(branch.holderDID) ? (
                  <div>
                    <button
                      style={{ width: "150px", height: "50px" }}
                      type="button"
                      className="btn btn-success"
                      onClick={() => {
                        // Handle the logic for legitimize action
                      }}
                    >
                      Legitimize
                    </button>
                    <br />
                    <br />
                    <button
                      style={{ width: "150px", height: "50px" }}
                      type="button"
                      className="btn btn-warning"
                      disabled
                    >
                      Send Invitation
                    </button>
                  </div>
                ) : (
                  <div>
                    <button
                      style={{ width: "150px", height: "50px" }}
                      type="button"
                      className="btn btn-success"
                      disabled
                    >
                      Legitimize
                    </button>
                    <br />
                    <br />
                    <button
                      style={{ width: "150px", height: "50px" }}
                      type="button"
                      className="btn btn-warning"
                      onClick={() => sendInvitation(branch.holderDID)}
                    >
                      Send Invitation
                    </button>
                  </div>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Viewstores;
