import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
// import { useNavigate } from "react-router-dom";

function Viewstores() {
  const [branches, setbranches] = useState([]);
  const navigate = useNavigate();
  // const [invitations, setInvitations] = useState([]);

  // const navigate = useNavigate();

  // const [showActionButtons, setShowActionButtons] = useState(true);

  useEffect(() => {
    // Fetch data from the backend server
    axios
      .get("http://localhost:3007/view-stores")
      .then((response) => {
        //setInvitations(response.data);
        const data = response.data;
        if (Array.isArray(data)) {
          setbranches(data);
        } else {
          console.error("branches data is not an array:", data);
        }
      })
      .catch((error) => {
        console.error("Failed to fetch stores:", error);
      });
  }, []);

  useEffect(() => {
    // Fetch data from the backend server
    axios
      .get("http://localhost:3007/view-invitations")
      .then((response) => {
        //setInvitations(response.data);
        const data = response.data;
        if (Array.isArray(data)) {
        } else {
          console.error("Invitations data is not an array:", data);
        }
      })
      .catch((error) => {
        console.error("Failed to fetch invitations:", error);
      });
  }, []);

  const sendInvitation = (holderDID) => {
    // send invitation to a holder based on their DID
    axios
      .post("http://localhost:3007/create-invitation", {
        did: holderDID,
        status: "pending",
      })
      .then((response) => {
        console.log("Invitation sent successfully");
        // Perform any additional actions if needed
      })
      .catch((error) => {
        console.error("Failed to send invitation:", error);
      });
  };

  return (
    <div className="container">
      <style
        successouslySetInnerHTML={{
          __html:
            "\n  .table-container {\n    max-width: 100%;\n    overflow-x: auto;\n  }\n\n  .table {\n    width: 100%;\n    table-layout: fixed;\n    border-collapse: collapse;\n  }\n\n  .table td {\n    word-wrap: break-word;\n    max-width: 200px; /* Adjust the value as needed */\n    white-space: normal;\n  }\n",
        }}
      />

      <table className="table">
        <caption>List of Franchise stores</caption>
        <thead>
          <tr>
            <th scope="col">#</th>
            <th scope="col">Branch Name</th>
            {/* <th scope="col">Invitation</th>*/}
            {/* <th scope="col">Branch Manager Username</th>  */}
            <th scope="col">Store Manager DID</th>
            <th scope="col">Store Manager Name</th>
            <th scope="col">Store's Login Credential</th>
            <th scope="col">Action</th>
          </tr>
        </thead>
        <tbody>
          {branches.map((branch, index) => (
            <tr key={branch._id}>
              <th scope="row">{index + 1}</th>
              <td>{branch.branchName}</td>
              {/* <td>{JSON.stringify(invitation.invitation)}</td>*/}
              {/* <td>
                <a href={invitation.invitation_url}>invitation</a>
              </td> */}
              <td>{branch.holderDID}</td>
              <td>{branch.branchManager}</td>
              <td>
                <button
                  type="button"
                  className="btn btn-light"
                  onClick={() => {
                    const did = branch.holderDID;
                    const bname = branch.branchName;
                    navigate(`/generate-cred/${bna}`);
                  }}
                >
                  Generate Store Default Credentials
                </button>
              </td>
              <td>
                {branch.invited === "no" ? (
                  <div>
                    <button
                      style={{ width: "150px", height: "50px" }}
                      type="button"
                      className="btn btn-warning"
                      onClick={() => sendInvitation(branch.holderDID)}
                    >
                      Send Invitation
                    </button>
                    &nbsp;&nbsp; <br></br> <br></br>
                    <button
                      style={{ width: "150px", height: "50px" }}
                      type="button"
                      className="btn btn-success"
                      disabled
                    >
                      Legitimize
                    </button>
                  </div>
                ) : (
                  <div>
                    <button
                      style={{ width: "150px", height: "50px" }}
                      type="button"
                      className="btn btn-warning"
                      onClick={() => sendInvitation(branch.holderDID)}
                      disabled
                    >
                      Send Invitation
                    </button>
                    &nbsp;&nbsp; <br></br> <br></br>
                    <button
                      style={{ width: "150px", height: "50px" }}
                      type="button"
                      className="btn btn-success"
                    >
                      Legitimize
                    </button>
                  </div>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Viewstores;
